import {useEffect, useState} from 'react'
import ComputingSteps from './ComputingSteps/ComputingSteps'
import PostLoadingComponent from './PostLoading'


const Cover = () => {
    const PostLoading = PostLoadingComponent(ComputingSteps);
    const [appState, setAppState] = useState({
        loading: false,
        posts: null,
    });
    useEffect(() => {
        setAppState({ loading: true });
        const apiUrl = `http://127.0.0.1:8000/api/`;
        fetch(apiUrl)
            .then((data) => data.json())
            .then((posts) => {
                setAppState({ loading: true, posts: posts });
            });
    }, [setAppState]);

    return (
        <div>
            <h1>steps</h1>
            <PostLoading isLoading = {appState.loading} posts = {appState.posts} />
        </div>
    );
}

export default Cover;
