import { useState } from 'react';

const Home = () => {

    const [steps, setSteps] = useState([
        {title: '1. Requirements', id: 1},
        {title: '2. School Officer', id: 2},
        {title: '3. Head of Department', id: 3},
        {title: '4. Babcock University Teaching Hospital', id: 4},
        {title: '5. Director, Student Support Services', id: 5},
        {title: '6. Director, Security Services', id: 6},
        {title: '7. Data and Archieves Officer', id: 7},
        {title: '8. Admissions Officer', id: 8},
        {title: '9. School Officer', id: 9},

    ]);
    return ( 
        <div className = "home">
           <div className = "black-look">
                <div class = "intro">
                    <p>Welcome to Fresherstrip,</p>
                    <p id = "say">We offer you the steps and the location to make your process faster.
                    Down here are the steps you need to take, click one them to get the directions.</p>
                </div>
            </div>
            
            {steps.map((step) => (
                <div className = "step-preview" key = {step.id}>
                    <h2>{ step.title }</h2>
                </div>
            ))}
        </div>
        
        
    );
}
 
export default Home;
