import { Link, useHistory } from 'react-router-dom';
import { useRef, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Helmet } from 'react-helmet';

const SignUp = () => {
    const emailRef = useRef()
    const passwordRef = useRef()
    const confirmPasswordRef = useRef()
    const { signup } = useAuth()
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const history = useHistory()

    async function handleSubmit(e) {
        e.preventDefault()

        if (passwordRef.current.value !== confirmPasswordRef.current.value){
            return setError("Passwords do not match")
        }
        if (passwordRef.current.value.length < 8){
            return setError("Password must be at least 8 characters ")
        }
        try{
            setError('')
            setLoading(true)
            await signup(emailRef.current.value, passwordRef.current.value)
            history.push("/SelectCourse")
        }
        catch{
            setError("Failed to create an account check connection or email")
            setLoading(false)
        }
    }

    return ( 
        <div className = "signup-login">
            <Helmet>
                <title>Sign Up | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Sign Up</p>

                {error && <p style = {{color: "red"}}>{ error }</p>}

                <form onSubmit = {handleSubmit}>
                    <label>Email</label>
                    <input type="email" ref = {emailRef}  required/>

                    <label>Password</label>
                    <input type="password" ref = {passwordRef} required/>

                    <label>Confirm Password</label>
                    <input type="password" ref = {confirmPasswordRef}  required/>  

                    {!loading && <button>Sign Up</button>}
                    {loading && <button disabled>Loading...</button>}
                </form>
            </div>
            <br />
            <p>Already have an account? <Link to = "/LogIn"><span style = {{color: "#f1356d"}}>Log In</span></Link></p>
        </div>
    );
}

export default SignUp;
