import { useRef, useEffect, useState } from "react";
import styled from "styled-components";
import Iframe from "react-iframe";

const DIV = styled.div`
    margin: 0 auto; 
    margin-top: 30px;
    max-width: 80%;
    padding: 20px;
`

const Paypal = () => {

    const paypal = useRef()
    const [paidFor, setPaidFor] = useState(false)
    const [error, setError] = useState(null)


    useEffect(() => {
        window.paypal.Buttons({
                createOrder: (data, actions, err) => {
                    return actions.order.create({
                        intent: "CAPTURE",
                        purchase_units: [
                            {
                                description: "Computing and Engineering Sciences",
                                amount: {
                                    currency_code: "USD",
                                    value: 1.2,
                                },
                            },
                        ],
                    });
                },
                onApprove: async (data, actions) => {
                    const order = await actions.order.capture();
                    setPaidFor(true)
                    console.log(order)
                },
                onError: (err) => {
                    setError(err);
                } 
        }).render(paypal.current)
    }, [paidFor, error]);

    if(paidFor){
        return (
            <Iframe>
                <div style = {{fontSize: "20px", marginTop: "30px", color: "green"}}>Payment Successful</div>
            </Iframe>
        );
    }
    return (  
        <DIV>
            <div ref = {paypal}></div>
        </DIV>
    );
}
 
export default Paypal;
