import styled from "styled-components";
import { useState } from 'react';
import { Link, useHistory } from "react-router-dom";
import { useAuth } from './contexts/AuthContext';


const DIV = styled.div`
    
    .email-logout{
        margin: 0 auto;
        display: flex;
        /* border: 1px solid black; */
        margin-top: 80px;
        justify-content: space-between;
        max-width: 95%;
    }
    #hi{
        padding-top: 10px;
        font-size: 17px;
        text-align: left;
        letter-spacing: -0.4px;
    }
    #logout{
        text-align: right;
        background: white;
        margin-right: 10px;
        border: 1px solid #f1356d;
        padding: 10px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.1s linear;
        color: #f1356d;
    }
    #logout:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    #head{
        margin-top: 50px;
        font-size: 35px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        transition: 0.2s all linear;
    }
    #course{
        border: 1px solid #f1356d;
        padding: 20px;
        font-size: 24px;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
    }
    #course:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
   
`
const SelectCourse = () => {
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth();
    const history = useHistory();

    async function handleLogout(){
        setError('')
        
        try{
            await logout()
            history.push('/')
        }
        catch{
            setError("Failed to log out")
        }
    }

    return (  
        <DIV className = "select-course">
            <div className = "email-logout">
                <p id = "hi">Welcome {currentUser.email},</p>
                <button onClick = {handleLogout} id = "logout">Log out</button>
            </div>
           
            <p id = "head">Select your course</p>
            <div className = "select">
                <Link to = "/Computing"><div id = "course">
                    Computing and Engineering Sciences<br />
                    Price: $1.2 = N500
                </div><br /></Link>
                <Link to = "/MassComm"><div id = "course">
                    Mass Communication<br />
                    Price: $1.2 = N500
                </div></Link>
            </div>
        </DIV>
    );
}
 
export default SelectCourse;
