import styled from "styled-components";
// import { useState } from 'react';
// import { useHistory } from "react-router-dom";
// import { useAuth } from './contexts/AuthContext';
import bucc from './image/bucc.webp'
import mass from './image/mass.webp'
// import Paypal from "./Paypal/Paypal";


const DIV = styled.div`
    .email-logout{
        margin: 0 auto;
        display: flex;
        /* border: 1px solid black; */
        margin-top: 80px;
        justify-content: space-between;
        max-width: 95%;
    }
    #hi{
        padding-top: 10px;
        font-size: 17px;
        text-align: left;
        letter-spacing: -0.4px;
    }
    #logout{
        text-align: right;
        background: white;
        margin-right: 10px;
        border: 1px solid #f1356d;
        padding: 10px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.1s linear;
        color: #f1356d;
    }
    @media(max-width: 700px){
        #hi{
            font-size: 13px;
        }
        #logout{
            font-size: 13px;
            padding: 6px;
        }
    }
    #logout:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    #head{
        margin-top: 50px;
        font-size: 35px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        width: 600px;
        transition: 0.2s all linear;
    }
    #course-price{
        border: none;
        box-shadow: 0px 0px 3px 0px grey;
        border-radius: 10px;
    }
    #course-price p{
        font-size: 16px;
    }
    #course{
        border: 1px solid #f1356d;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
        padding: 8px;
    }
    #course p{
        font-size: 19px;
    }
    #course:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    img{
        border-radius: 5px;
    }
   
`
const SelectCourse = () => {
   
    return (  
        <DIV className = "select-course">
            <div className = "email-logout">
                {/* {error && <p style = {{color: "red"}}>{ error }</p>} */}

                <p id = "hi">Hi</p>
                <button id = "logout">Log out</button>
            </div>
            
            <p id = "head">Select your course</p>
            <div className = "select">
                <div id = "course-price">
                    <a href = "https://paystack.com/pay/x-4swryp-d"><div id = "course">
                        <img src={bucc} width = "50px" alt="bucc"/>
                        <p>Computing and Engineering Sciences</p>
                    </div></a>
                    <br />
                    <div>
                        <p>N600/Monthly</p><br />
                    </div>
                </div>
                <br /><br />
                <div id = "course-price">
                    <a href = "https://paystack.com/pay/aq2hl8ahum"><div id = "course">
                        <img src={mass} width = "30px" alt="mass"/>
                        <p>Mass Communication</p>
                    </div></a><br />
                    <div>
                        <p>N600/Monthly</p><br />
                    </div>
                </div>
            </div>
        </DIV>
    );
}
 
export default SelectCourse;
