import { Link } from 'react-router-dom';
import { useState } from 'react';
import { Helmet } from 'react-helmet';
import axiosInstance from '../axios';
import { useHistory } from 'react-router-dom'

const LogIn = () => {

    const history = useHistory();
	const initialFormData = Object.freeze({
		email: '',
		password: '',
	});

	const [formData, updateFormData] = useState(initialFormData);
    const [loading, setLoading] = useState(false);


	const handleChange = (e) => {
		updateFormData({
			...formData,
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
        setLoading(true);
		console.log(formData);

		axiosInstance
			.post(`token/`, {
				email: formData.email,
				password: formData.password,
			})
			.then((res) => {
				localStorage.setItem('access_token', res.data.access);
				localStorage.setItem('refresh_token', res.data.refresh);
				axiosInstance.defaults.headers['Authorization'] =
					'JWT ' + localStorage.getItem('access_token');
				history.push('/');
				//console.log(res);
				//console.log(res.data);
			});
	};
 
    return (  
        <div className = "signup-login">
             <Helmet>
                <title>Log In | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Log In</p>
                <p>Log into your account</p><br />

                {/* {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>} */}

                <form>
                    <label>Email</label>
                    <input type="email" name = "email"  placeholder = "Email" required/>

                    <label>Password</label>
                    <input type="password" name = "password" placeholder = "Password" minLength = '6' required/> 

                    <button type = "submit">Log In</button>
                    {loading && <button disabled>Loading...</button>}<br /><br /> 
                    
                    <Link to = "/reset-password"><p style = {{color: "#f1356d"}}>Forgot Password?</p></Link>
                </form>
            </div>
            <br />
            <p>Don't have an account? <Link to = "/SignUp"><span style = {{color: "#f1356d"}}>Sign Up</span></Link></p>
        </div>
    );
}


export default LogIn;
