import styled from "styled-components";
import { useState } from 'react';
import { Link, useHistory } from "react-router-dom";
import { useAuth } from './contexts/AuthContext';


const DIV = styled.div`
    
    .email-logout{
        margin: 0 auto;
        display: flex;
        /* border: 1px solid black; */
        margin-top: 80px;
        justify-content: space-between;
        max-width: 95%;
    }
    #hi{
        padding-top: 10px;
        font-size: 17px;
        text-align: left;
        letter-spacing: -0.4px;
    }
    #logout{
        text-align: right;
        background: white;
        margin-right: 10px;
        border: 1px solid #f1356d;
        padding: 10px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.1s linear;
        color: #f1356d;
    }
    #logout:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    #head{
        margin-top: 50px;
        font-size: 35px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        transition: 0.2s all linear;
    }
    #course{
        border: 1px solid #f1356d;
        padding: 20px;
        font-size: 24px;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
    }
    #course:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }

    #turnon{
        margin: 0 auto;
        margin-top: 10px;
        font-size: 17px;
        text-align: right;
        max-width: 95%;
    }
    #step-header{
        margin-top: 50px;

        font-size: 37px;
    }
    .step-preview{
        margin: 0 auto;
        max-width: 100%;
        width: 700px;
    }
    .step-preview p{
        padding: 10px;
        margin: 30px 60px;
        border: none;
        background-color: rgb(255, 255, 255);
        border-radius: 10px;
        color: #f1356d;
        font-size: 23px;
        box-shadow: 0px 1px 2px 0px #f1356d;
    }
    .step-preview p:hover{
        background-color: rgb(0, 0, 0);
        border-color: black;
        transition: all 0.2s linear;
        color: white;
        box-shadow: 0px 1px 2px 0px black;
    }
   
`
const ComputingSteps = () => {
    const [error, setError] = useState("")
    const { currentUser, logout } = useAuth();
    const history = useHistory();

    async function handleLogout(){
        setError('')
        
        try{
            await logout()
            history.push('/')
        }
        catch{
            setError("Failed to log out")
        }
    }

    return (  
        <DIV className = "select-course">
            <div className = "email-logout">
                <p id = "hi">Welcome {currentUser.email},</p>
                <button onClick = {handleLogout} id = "logout">Log out</button>
            </div>
            
            <p id = "turnon">Turn on your device location</p>           
            <p id = "head">Computing and Engineering Sciences</p>
            <p id = "step-header">Steps</p>
            <div className = "step-preview">
                <Link to = '/Requirements'><p>Requirements</p></Link>
                <Link to = '/SchoolOfficer'><p>1. School Officer</p></Link>
                <Link to = '/HeadOfDepartment'><p>2. Head of Department</p></Link>
                <Link to = '/BabcockUniversityTeachingHospital'><p>3. Babcock University Teaching Hospital</p></Link>
                <Link to = '/DirectorStudentSupportServices'><p>4. Director, Student Support Services</p></Link>
                <Link to = '/DirectorSecurityServices'><p>5. Director, Security Services</p></Link>
                <Link to = '/DataandArchievesOfficer'><p>6. Data and Archieves Officer</p></Link>
                <Link to = '/AdmissionsOfficer'><p>7. Admissions Officer</p></Link>
                <Link to = '/SchoolOfficer2'><p>8. School Officer</p></Link>
                <a href = 'https://umis.babcock.edu.ng/babcock/'><p>Online Registration</p></a>
            </div>
        </DIV>
    );
}
 
export default ComputingSteps;
