import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { useState } from 'react'
import { signup } from '../actions/auth';
import { Helmet } from 'react-helmet';

const SignUp = ({ signup, isAuthenticated }) => {
    const [accountCreated, setAccountCreated] = useState(false);

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        re_password: ''
    });

    const { name, email, password, re_password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value});

    const onSubmit = e => {
        e.preventDefault();

        if(password === re_password){
            signup(name, email, password, re_password);
            setAccountCreated(true)
        }

    };

    if (isAuthenticated){
        return <Redirect to = '/' />
    }
    if (accountCreated){
        return <Redirect to = '/ActivateLink' />
    }
        
    return ( 
        <div className = "signup-login">
            <Helmet>
                <title>Sign Up | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Sign Up</p>
                <p>Create an account</p>
                {/* {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>} */}

                <form onSubmit = {e => onSubmit(e)}>
                    <label>Name</label>
                    <input type="text" name = 'name' value = {name} placeholder = "Name*" onChange = {e => {onChange(e)}} required/>

                    <label>Email</label>
                    <input type="email" name = 'email' value = {email} placeholder = "Email*" onChange = {e => {onChange(e)}} required/>

                    <label>Password</label>
                    <input type="password" name = 'password' value = {password} placeholder = "Password*" onChange = {e => {onChange(e)}} minLength = '6' required/>

                    <label>Confirm Password</label>
                    <input type="password" name = 're_password' value = {re_password} placeholder = "Confirm Password*" onChange = {e => {onChange(e)}} minLength = '6' required/>  

                    <button>Sign Up</button>
                </form>
            </div>
            <br />
            <p>Already have an account? <Link to = "/LogIn"><span style = {{color: "#f1356d"}}>Log In</span></Link></p>
        </div>
    );
}

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { signup })(SignUp);
