import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import {useState} from 'react';
import  axiosInstance  from '../axios';
import { useHistory } from 'react-router-dom';

const SignUp = () => {

    const history = useHistory();
	const initialFormData = Object.freeze({
		email: '',
		username: '',
		password: '',
	});

	const [formData, updateFormData] = useState(initialFormData);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [loading, setLoading] = useState(false);


	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
        setLoading(true);
		// console.log(formData);

		axiosInstance
			.post(`user/create/`, {
				email: formData.email,
				user_name: formData.username,
				password: formData.password,
			})
			.then((res) => {
                setLoading(false);
				history.push('/Login');
                setIsAuthenticated(true);
				// console.log(res);
				// console.log(res.data);
			});
        
        axiosInstance
            .catch(function (error) {
                if (error.response) {
                  // The request was made and the server responded with a status code
                  // that falls out of the range of 2xx
                  console.log(error.response.data);
                  console.log(error.response.status);
                  console.log(error.response.headers);
                } else if (error.request) {
                  // The request was made but no response was received
                  // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
                  // http.ClientRequest in node.js
                  console.log(error.request);
                } else {
                  // Something happened in setting up the request that triggered an Error
                  console.log('Error', error.message);
                }
                console.log(error.config);
              });
	};

   
    return ( 
        <div className = "signup-login">
            <Helmet>
                <title>Sign Up | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Sign Up</p>
                <p>Create an account</p><br />
                {/* {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>} */}

                <form>
                    {/* <label>Name</label>
                    <input type="text" id = "name" placeholder = "Name*" autoComplete = "name" onChange = {handleChange} required/> */}
                    
                    <label>Email</label>
                    <input type="email" name = "email" placeholder = "Email*" autoComplete = "email" onChange = {handleChange} required/>

                    <label>Username</label>
                    <input type="text" name = "username" placeholder = "Username*" autoComplete = "username" onChange = {handleChange} required/>

                    <label>Password</label>
                    <input type="password" name = "password" placeholder = "Password*"  minLength = '6' autoComplete = "current-password" onChange = {handleChange} required/>

                    {/* <label>Confirm Password</label>
                    <input type="password" placeholder = "Confirm Password*"  minLength = '6' required/>   */}

                    {loading && <button disabled>Loading...</button>}
                    {!loading && <button type = "submit" onClick = {handleSubmit}>Sign Up</button>}
                </form>
            </div>
            <br />
            <p>Already have an account? <Link to = "/LogIn"><span style = {{color: "#f1356d"}}>Log In</span></Link></p>
        </div>
    );
}



export default SignUp;
