import { Link, Redirect } from 'react-router-dom';
import { useState } from 'react';
import { Helmet } from 'react-helmet';
import { connect } from 'react-redux';
import { login } from '../actions/auth';
import PropTypes from 'prop-types';

const LogIn = ({ login, isAuthenticated }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const { email, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value});

    const onSubmit = e => {
        e.preventDefault();

        login(email, password);
    };

    if (isAuthenticated){
        return <Redirect to = '/' />
    }

    return (  
        <div className = "signup-login">
             <Helmet>
                <title>Log In | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Log In</p>
                <p>Log into your account</p>

                {/* {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>} */}

                <form onSubmit = {e => onSubmit(e)}>
                    <label>Email</label>
                    <input type="email" name = 'email' value = {email} placeholder = "Email" onChange = {e => {onChange(e)}} required/>

                    <label>Password</label>
                    <input type="password" name = 'password' value = {password} placeholder = "Password" onChange = {e => {onChange(e)}} minLength = '6' required/> 

                    <button type = "submit">Log In</button>
                    {/* {loading && <button disabled>Loading...</button>}*/}<br /><br /> 
                    
                    <Link to = "/reset-password"><p style = {{color: "#f1356d"}}>Forgot Password?</p></Link>
                </form>
            </div>
            <br />
            <p>Don't have an account? <Link to = "/SignUp"><span style = {{color: "#f1356d"}}>Sign Up</span></Link></p>
        </div>
    );
}

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated
});

export default connect(mapStateToProps, { login })(LogIn);
