import { Link } from 'react-router-dom';
import { useRef, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

const ForgotPassword = () => {
    const emailRef = useRef()
    const { resetPassword } = useAuth()
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const [message, setMessage] = useState('')

    async function handleSubmit(e){
        e.preventDefault()

        try{
            setMessage('')
            setError('')
            setLoading(true)
            await resetPassword(emailRef.current.value)
            setMessage('Check your inbox for further instructions')
        } catch {
            setError('Failed to reset password')
        }

        setLoading(false)
    }
    return (  
        <div className = "signup-login">
            <div className = "create">
                <p className = "create-header">Password Reset</p>

                {error && <p variant="danger" style = {{color: "red"}}>{ error }</p>}

                <form onSubmit = { handleSubmit }>
                    <label>Email</label>
                    <input type="email"  ref = {emailRef} required/>

            
                    <button style = {{width: '200px'}} disabled = {loading}>Reset Password</button>
                    <br /><br />
                    <Link to = "/LogIn"><p style = {{color: "#f1356d"}}>Log In</p></Link>
                </form>
            </div>
            <br />
            <p>Need an account? <Link to = "/SignUp"><span style = {{color: "#f1356d"}}>Sign Up</span></Link></p>

        </div>
    );
}
 
export default ForgotPassword;
