import { Link } from 'react-router-dom';
// import { useRef, useState } from 'react';
// import { useAuth } from '../contexts/AuthContext';
import { Helmet } from 'react-helmet';

const SignUp = () => {
    
    return ( 
        <div className = "signup-login">
            <Helmet>
                <title>Sign Up | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Sign Up</p>
                <p>Create an account</p>
                {/* {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>} */}

                <form>
                    <label>Email</label>
                    <input type="email" placeholder = "Email" required/>

                    <label>Password</label>
                    <input type="password" placeholder = "Password" required/>

                    <label>Confirm Password</label>
                    <input type="password" placeholder = "Confirm Password" required/>  

                    <button>Sign Up</button>
                </form>
            </div>
            <br />
            <p>Already have an account? <Link to = "/LogIn"><span style = {{color: "#f1356d"}}>Log In</span></Link></p>
        </div>
    );
}
export default SignUp;
