import { PaystackButton } from 'react-paystack'
import React, { useState } from "react"
import styled from 'styled-components';
import { Redirect } from 'react-router-dom'
import bucc from './image/bucc.webp'

const DIV = styled.div`
   @import url("https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,400;1,300&display=swap");
    
    margin: 0 auto;
    margin-top: 50px;
    text-align: center;
    font-family: "Roboto";
    letter-spacing: 0.1rem;
    width: 80%;
    max-width: 600px;
    box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.5);
    
   
    .item-image {
        width: 40%;
        object-fit: cover;
    }
    form{
        margin: 0 auto;
        background: black;
        height: 200px;
    }
    
    label {
      text-align: left;
      display: block;
    }   
    input {
      width: 100%;
      padding: 6px 10px;
      margin: 10px 0;
      border: 1px solid rgb(206, 204, 204);
      box-sizing: border-box;
      display: block;
      text-align: left;
      border-radius: 5px;
    }
    .paystack-button {
        margin: 0 auto;
        cursor: pointer;
        text-align: center;
        font-size: 15px;
        letter-spacing: 0.1rem;
        text-transform: uppercase;
        background-color: #f1356d;
        font-weight: bold;
        color: white;
        border-radius: 5px;
        width: 90%;
        height: 45px;
        margin-top: 40px;
        border: none;
    }
`

const Paystack = () => {
  
    const publicKey = `pk_test_f93a2b4fbfdae5834eb1f3fe95728e99cf63b8bd`
    const amount = 50000
    const [email, setEmail] = useState("")
    const [name, setName] = useState("")
    const [phone, setPhone] = useState("")
    const componentProps = {
      email,
      amount,
      metadata: {
        name,
        phone,
      },
      publicKey,
      reference: ''+Math.floor((Math.random() * 1000000000000) + 1),
      text: "Pay Now",
      onSuccess: () => {
        alert("Thanks for the purchase")
      },

      onClose: () => alert("Hope to see you later"),
    }

    if (localStorage.getItem('access_token') === null){
      alert("User not logged In");
      return <Redirect to = '/' />
    }

    return (
      <DIV className="container">
        <img className="item-image" src={bucc} alt="product"/>
        <form>
          <label>Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <label>Email</label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label>Phone</label>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
          <PaystackButton className="paystack-button" {...componentProps} />
        </form>
      </DIV>
    )
  }
 
export default Paystack;
