import styled from "styled-components";
import bucc from './image/bucc.webp'
import mass from './image/mass.webp'
import { Link, Redirect } from 'react-router-dom'

const DIV = styled.div`
    .email-logout{
        margin: 0 auto;
        display: flex;
        margin-top: 80px;
        justify-content: space-between;
        max-width: 95%;
    }
    .header{
        display: flex;
        width: 100%;
        justify-content: space-between;
    }
    #hi{
        padding-top: 10px;
        font-size: 17px;
        text-align: left;
        letter-spacing: -0.4px;
    }
    #logout{
        background: white;
        border: 1px solid #f1356d;
        padding: 8px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.2s linear;
        color: #f1356d;
    }
    #logout:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    #head{
        margin-top: 50px;
        font-size: 30px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        width: 600px;
        transition: 0.2s all linear;
    }
    #course-price{
        border: none;
        box-shadow: 0px 0px 3px 0px grey;
        border-radius: 10px;
    }
    #course-price p{
        font-size: 16px;
    }
    #course{
        border: 1px solid pink;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
        padding: 8px;
    }
    #course p{
        font-size: 19px;
    }
    #course:hover{
        background: pink;
        border: 1px solid pink;
        color: white;
    }
    img{
        border-radius: 5px;
    }
   
`
const SelectCourse = () => {

    if (localStorage.getItem('access_token') === null){
        alert("User not logged In");
        return <Redirect to = '/' />
    }

    return (  
        <DIV className = "select-course">
            <div className = "email-logout">
                <div className = "header">
                    <p id = "hi">Welcome,</p>
                    <Link to = "/Logout"><button id = "logout" >Log out</button></Link>
                </div>
            </div>
            
            <p id = "head">Select your course</p>
            <div className = "select">
                <div id = "course-price">
                    <Link to = "/PayComputing">
                        <div id = "course">
                        <img src={bucc} width = "50px" alt="bucc"/>
                        <p>Computing and Engineering Sciences</p>
                        </div>
                    </Link><br />
                    <div>
                        <p>N500/Monthly</p><br />
                    </div>
                </div>
                <br /><br />
                <div id = "course-price">
                    <Link to = "/PayMass">
                        <div id = "course">
                        <img src={mass} width = "30px" alt="mass"/>
                        <p>Mass Communication</p>
                    </div>
                    </Link><br />
                    <div>
                        <p>N500/Monthly</p><br />
                    </div>
                </div>
            </div>
        </DIV>
    );
}

 
export default SelectCourse;
