import { PaystackButton } from 'react-paystack'
import React, { useState } from "react"
import styled from 'styled-components';
import { Redirect } from 'react-router-dom'
import bucc from './image/bucc.webp'

const DIV = styled.div`
   @import url("https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,400;1,300&display=swap");
    
    margin: 0 auto;
    margin-top: 50px;
    text-align: center;
    font-family: "Roboto";
    letter-spacing: 0.1rem;
    width: 80%;
    max-width: 600px;
    box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.5);
    
   
    .item-image {
        width: 40%;
        object-fit: cover;
    }
    .overlay-effect {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        opacity: 0.2;
        overflow: hidden;
        z-index: 1;
    }

    .checkout {
        margin: 0 auto;
        background: black;
        display: flex;
        flex-direction: column;
        justify-content: center;
        height: 430px;
        width: 100%;
    }
    .checkout-form {
        padding: 20px 20px;
        display: flex;
        flex-direction: column;
        margin-bottom: 20px;
    }
    .checkout-form label {
        text-align: left;
        color: white;
        font-size: 10px;
        margin-bottom: 5px;
        text-transform: uppercase;
        letter-spacing: 0.1rem;
    }   
    .checkout-form input {
        background-color: white;
        border: 1px solid #cecece;
        border-radius: 5px;
        color: #e0eafc;
        height: 35px;
    }
    .paystack-button {
        cursor: pointer;
        text-align: center;
        font-size: 10px;
        letter-spacing: 0.1rem;
        text-transform: uppercase;
        background-color: pink;
        font-weight: bold;
        color: pink;
        border: none;
        border-radius: 5px;
        width: 100%;
        height: 45px;
        margin-top: 40px;
    }
`

const Paystack = () => {
  
    const publicKey = `pk_test_f93a2b4fbfdae5834eb1f3fe95728e99cf63b8bd`
    const amount = 50000
    const [email, setEmail] = useState("")
    const [name, setName] = useState("")
    const [phone, setPhone] = useState("")
    const componentProps = {
      email,
      amount,
      metadata: {
        name,
        phone,
      },
      publicKey,
      reference: ''+Math.floor((Math.random() * 1000000000000) + 1),
      text: "Pay Now",
      onSuccess: () => {
        alert("Thanks for the purchase")
      },

      onClose: () => alert("Hope to see you later"),
    }

    if (localStorage.getItem('access_token') === null){
      alert("User not logged In");
      return <Redirect to = '/' />
    }

    return (
        <DIV className="container">
        <div className="item">
          <div className="overlay-effect"></div>
          <img
            className="item-image"
            src={bucc}
            alt="product"
          />
          
        </div>
        <div className="checkout">
          <div className="checkout-form">
            <div className="checkout-field">
              <label>Name</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Email</label>
              <input
                type="text"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Phone</label>
              <input
                type="text"
                id="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </div>
            <PaystackButton className="paystack-button" {...componentProps} />
          </div>
        </div>
      </DIV>
    )
  }
 
export default Paystack;
