import { PaystackButton } from 'react-paystack'
import React, { useState } from "react"
import styled from 'styled-components';


const DIV = styled.div`
   @import url("https://fonts.googleapis.com/css2?family=Roboto:ital,wght@0,400;1,300&display=swap");
    
    text-align: center;
    font-family: "Roboto";
    letter-spacing: 0.1rem;
    
    .container {
        display: flex;
        flex-direction: row;
        margin: 5% auto;
        width: 635px;
        height: 430px;
        background: white;
        box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.5);
    }
    .item {
        width: 50%;
        position: relative;
    }
    .item-image {
        height: 430px;
        width: 100%;
        object-fit: cover;
    }
    .overlay-effect {
        position: absolute;
        top: 0;
        bottom: 0;
        left: 0;
        right: 0;
        opacity: 0.2;
        background-color: #303030;
        overflow: hidden;
        z-index: 1;
    }
    .item-details {
        position: absolute;
        bottom: 0;
        margin-bottom: 5px;
        margin-left: 20px;
        color: #84a17d;
        text-align: left;
    }
    .item-details__title {
    
    }
    .item-details__amount {
    
    }
    .checkout {
        background: #84a17d; /* fallback for old browsers */
        display: flex;
        flex-direction: column;
        justify-content: center;
        height: 430px;
        width: 50%;
    }
    .checkout-form {
        padding: 20px 20px;
        display: flex;
        flex-direction: column;
        margin-bottom: 20px;
    }
    .checkout-form label {
        text-align: left;
        color: #e0eafc;
        font-size: 10px;
        margin-bottom: 5px;
        text-transform: uppercase;
        letter-spacing: 0.1rem;
    }   
    .checkout-form input {
        background-color: transparent;
        border: 1px solid #cecece;
        border-radius: 5px;
        color: #e0eafc;
        height: 35px;
    }
    .paystack-button {
        cursor: pointer;
        text-align: center;
        font-size: 10px;
        letter-spacing: 0.1rem;
        text-transform: uppercase;
        background-color: #bfbfbf;
        font-weight: bold;
        color: #e0eafc;
        border: none;
        border-radius: 5px;
        width: 100%;
        height: 45px;
        margin-top: 40px;
    }
`

const Paystack = () => {

    const publicKey = process.env.REACT_APP_PAYSTACK_PUBLIC_KEY
    const amount = 1000000
    const [email, setEmail] = useState("")
    const [name, setName] = useState("")
    const [phone, setPhone] = useState("")
    const componentProps = {
      email,
      amount,
      metadata: {
        name,
        phone,
      },
      publicKey,
      text: "Pay Now",
      onSuccess: () =>
        alert("Thanks for the purchase"),
      onClose: () => alert("Hope to see you later"),
    }
    return (
      <DIV className="App">
        <div className="container">
        <div className="item">
          <div className="overlay-effect"></div>
          <img
            className="item-image"
            src="https://images.unsplash.com/photo-1526947425960-945c6e72858f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2850&q=80"
            alt="product"
          />
          <div className="item-details">
            <p className="item-details__title">Coconut Oil</p>
            <p className="item-details__amount">NGN {amount / 100}</p>
          </div>
        </div>
        <div className="checkout">
          <div className="checkout-form">
            <div className="checkout-field">
              <label>Name</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Email</label>
              <input
                type="text"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Phone</label>
              <input
                type="text"
                id="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </div>
            <PaystackButton className="paystack-button" {...componentProps} />
          </div>
        </div>
      </div>
      </DIV>
    )
  }
 
export default Paystack;
