    return ( 
        <div className = "signup-login">
            <div className = "create">
                <p className = "create-header">Sign Up</p>

                {/* {error && <p style = {{color: "red"}}>{ error }</p>} */}

                <form>
                    <label>Email</label>
                    <input type="email"   required/>

                    <label>Password</label>
