// import ScrollToTop from './scrolltotop/scrollToTop';
import { Helmet } from 'react-helmet';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';

const DIV = styled.div`
    .black-look{
        text-align: left;
        color: white;
        background-color: black;
        border-radius: 50px;
        margin-top: 3px;
        margin-bottom: 50px;
        padding: 50px;
        font-size: 40px;
    }
    .black-look p{
        color: white;
        margin-bottom: 40px;
    }
    #say{
        font-size: 18px;
    }
    #sign-up{
        background: white;
        margin-top: 20px;
        margin-right: 10px;
        border: 1px solid #f1356d;
        padding: 10px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.1s linear;
        color: #f1356d;
    }
    #sign-up:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }
    #log-in{
        background: black;
        margin-top: 20px;
        border: 1px solid black;
        padding: 10px;
        border-radius: 40px;
        cursor: pointer;
        font-size: 15px;
        transition: all 0.1s linear;
        color: white;
    }
    #log-in:hover{
        background: white;
        border: 1px solid #f1356d;
        color: #f1356d;
    }

    .card{
        margin: 0 auto;
        max-width: 70%;
        border: none;
        box-shadow: 0px 0px 2px 0px;
        margin-top: 80px;
        padding: 50px 20px 50px 20px;
        border-radius: 20px;
    }
    #create{
        margin-top: 80px;
        font-size: 20px;
    }
    #we{
        font-size: 30px;
    }
    #to{
        margin-top: 40px;
        font-size: 25px;
    }
    #div-circle{
        margin-top: 40px;
    }
    #circles{
        margin: 0 auto;
        max-width: 50%;
        border: 1px solid #f1356d;
        padding: 20px;
        border-radius: 70px;
        font-size: 20px;
        color: #f1356d;
    }
`
const Home = () => {
    const { currentUser } = useAuth()

    if ( currentUser ){
        return (
            <DIV className = "home">
                <Helmet>
                    <title>Home | Fresherstrip</title>
                </Helmet>
                <div className = "black-look">
                    <p>Welcome to Fresherstrip,</p>
                    <p id = "say">We offer Babcock University 100 level School of Computing and Engineering 
                    Sciences and Mass Comm the steps and location to make your signing process faster.</p>                
                </div>
                 
                <Link to = "/AboutUs"><p id = "create">View Profile</p></Link>
                <button id = "sign-up">Profile</button>
    
                <div className = "card">
                    <p id = "we">We provide the,</p>
                    <div id = "div-circle">
                        <div id = "circles">Steps</div><br />
                        <div id = "circles">Locations</div>
                    </div>
                    <p id = "to">to make your signing process faster.</p>
                </div>
                
                {/* <ScrollToTop></ScrollToTop> */}
            </DIV>
        );
    }else{
        return (
        
            <DIV className = "home">
                <Helmet>
                    <title>Home | Fresherstrip</title>
                </Helmet>
                <div className = "black-look">
                    <p>Welcome to Fresherstrip,</p>
                    <p id = "say">We offer Babcock University 100 level School of Computing and Engineering 
                    Sciences and Mass Comm the steps and location to make your signing process faster.</p>                
                </div>
                 
                <p id = "create">Create an account or Login to start</p>
                <Link to = "/SignUp"><button id = "sign-up">Create Account</button></Link>
                <Link to = "/LogIn"><button id = "log-in">Log In</button></Link>
    
                <div className = "card">
                    <p id = "we">We provide the,</p>
                    <div id = "div-circle">
                        <div id = "circles">Steps</div><br />
                        <div id = "circles">Locations</div>
                    </div>
                    <p id = "to">to make your signing process faster.</p>
                </div>
                
                {/* <ScrollToTop></ScrollToTop> */}
            </DIV>
        );
    }    
    
}
 
export default Home;
