import styled from "styled-components";
// import { useState } from 'react';
import { Link } from "react-router-dom";
import Steps from './steps/Steps';


const DIV = styled.div`
    #hi{
        margin: 0 auto;
        margin-top: 80px;
        font-size: 20px;
        text-align: left;
        max-width: 85%;
        letter-spacing: -1px;
    }
    #head{
        margin-top: 50px;
        font-size: 35px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        transition: 0.2s all linear;
    }
    #course{
        border: 1px solid #f1356d;
        padding: 20px;
        font-size: 24px;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
    }
    #course:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }

`
const SelectCourse = () => {

    return (  
        <DIV className = "select-course">
            <p id = "hi">Welcome name,</p>
            <p id = "head">Select your course</p>
            <div className = "select">
                <Link to = "/Computing"><div id = "course">
                    Computing and Engineering Sciences<br />
                    Price: $1.2 = N500
                </div><br /></Link>
                <Link to = "/MassComm"><div id = "course">
                    Mass Communication<br />
                    Price: $1.2 = N500
                </div></Link>
            </div>

            <Steps></Steps>

        </DIV>
    );
}
 
export default SelectCourse;
