import { Link, useHistory } from 'react-router-dom';
import { useRef, useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Helmet } from 'react-helmet';

const LogIn = () => {
    const emailRef = useRef()
    const passwordRef = useRef()
    const { login } = useAuth()
    const [error, setError] = useState('')
    const [loading, setLoading] = useState(false)
    const history = useHistory()

    async function handleSubmit(e) {
        e.preventDefault()
        
        try{
            setError('')
            setLoading(true)
            await login(emailRef.current.value, passwordRef.current.value)
            history.push("/SelectCourse")
        }
        catch{
            setError("Failed to log in check connection, password or email")
            setLoading(false)
        }
    }

    return (  
        <div className = "signup-login">
             <Helmet>
                <title>Log In | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Log In</p>

                {error && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>{ error }</p>}

                <form onSubmit = {handleSubmit}>
                    <label>Email</label>
                    <input type="email" ref = {emailRef}  required/>
                    <label>Password</label>
                    <input type="password" ref = {passwordRef} required/> 

                    {!loading && <button>Log In</button>}
                    {loading && <button disabled>Loading...</button>}<br /><br />
                    
                    <Link to = "/ForgotPassword"><p style = {{color: "#f1356d"}}>Forgot Password?</p></Link>
                </form>
            </div>
            <br />
            <p>Need an account? <Link to = "/SignUp"><span style = {{color: "#f1356d"}}>Sign Up</span></Link></p>
        </div>
    );
}
export default LogIn;
