import { PayPalButton } from "react-paypal-button-v2";
import React from 'react';

export function PayPalBtn(props) {
    const { amount, currency, createSubscription, onApprove, catchError,onError, onCancel} = props;
    const paypalKey = "AVqbQbCd8K-3HvsQ3YTJ_SVJd56rx0DwZUzsQXGn2GY_nzoSH5yWbcsFQjfaNCG9H6XCgsc8Rb5I9oaK&vault=true&intent=subscription"
    
    return (
        <PayPalButton
            amount={amount}
            currency={currency}
            createSubscription={(data, details) => createSubscription(data, details)}
            onApprove={(data, details) => onApprove(data, details)}
            onError={(err) => onError(err)}
            catchError={(err) => catchError(err)}
            onCancel={(err) => onCancel(err)}
            options={{
            clientId: paypalKey,
            vault:true
            }}
            style={{
                marginTop: "10px",
                shape: 'pill',
                color: 'gold',
                layout: 'vertical',
                label: 'subscribe'
            }}
        />
    );
}
export default PayPalBtn;
