import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import {useState} from 'react';
import  axiosInstance  from '../axios';
import { useHistory } from 'react-router-dom';
// import axios from 'axios';

const SignUp = () => {

    const history = useHistory();
	const initialFormData = Object.freeze({
		email: '',
		username: '',
		password: '',
        // password2: '',

	});

	const [formData, updateFormData] = useState(initialFormData);
    const [errors, setErrors] = useState('');
    const [loading, setLoading] = useState(false);
    // const [successful, setSuccessful] = useState(false);



	const handleChange = (e) => {
		updateFormData({
			...formData,
			// Trimming any whitespace
			[e.target.name]: e.target.value.trim(),
		});
	};

	const handleSubmit = (e) => {
		e.preventDefault();
        setLoading(true);
		// console.log(formData);
        
        axiosInstance
            .post(`user/create/`, {
                email: formData.email,
                user_name: formData.username,
                password: formData.password,
                // password2: formData.password2,

            })
            .then((res) => {
                setLoading(false);
                // setIsAuthenticated(true);
                // console.log(res);
                // console.log(res.data);
            })
        
            // .catch(error => {
            //    setErrors(error)
            // });
            if(setLoading === false){
                history.push('/Login');

            }else{
                setErrors(true)

                setTimeout(() => {
                    setErrors(false)
                }, 2000);                
                
                setLoading(false)
            }
	};

   
    return ( 
        <div className = "signup-login">
            <Helmet>
                <title>Sign Up | Fresherstrip</title>
            </Helmet>
            <div className = "create">
                <p className = "create-header">Sign Up</p>
                <p>Create an account</p><br />
                {errors === true && <p style = {{color: "red", border: "1px solid red", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}> Error Authenticating, Check email or password should not be less than 6 characters</p>}
                {/* {successful === true && <p style = {{color: "green", border: "1px solid green", padding: "10px", borderRadius: "5px", marginBottom: "15px"}}>Account created</p>} */}

                <form>
                    {/* <label>Name</label>
                    <input type="text" id = "name" placeholder = "Name*" autoComplete = "name" onChange = {handleChange} required/> */}
                    
                    <label>Email</label>
                    <input type="email" name = "email" placeholder = "Email*" autoComplete = "email" onChange = {handleChange} required/>

                    <label>Username</label>
                    <input type="text" name = "username" placeholder = "Username*" autoComplete = "username" onChange = {handleChange} required/>

                    <label>Password</label>
                    <input type="password" name = "password" placeholder = "Password*"  minLength = '6' autoComplete = "current-password" onChange = {handleChange} required/>

                    {/* <label>Confirm Password</label>
                    <input type="password" name = "password2" placeholder = "Confirm Password*"  minLength = '6' autoComplete = "current-password" onChange = {handleChange} required/>   */}

                    {loading && <button disabled>Loading...</button>}
                    {!loading && <button type = "submit" onClick = {handleSubmit}>Sign Up</button>}
                </form>
            </div>
            <br />
            <p>Already have an account? <Link to = "/LogIn"><span style = {{color: "#f1356d"}}>Log In</span></Link></p>
        </div>
    );
}



export default SignUp;
