import styled from "styled-components";
import { useState } from 'react';

const DIV = styled.div`
    #hi{
        margin: 0 auto;
        margin-top: 80px;
        font-size: 20px;
        text-align: left;
        max-width: 85%;
    }
    #head{
        margin-top: 50px;
        font-size: 35px;
    }
    .select{
        margin: 0 auto;
        margin-top: 50px;
        max-width: 70%;
        transition: 0.2s all linear;
    }
    #course{
        border: 1px solid #e45a83;
        padding: 20px;
        font-size: 24px;
        border-radius: 10px;
        transition: 0.2s all linear;
        cursor: pointer;
    }
    #course:hover{
        background: black;
        border: 1px solid black;
        color: white;
    }

`
const SelectCourse = () => {

    return (  
        <DIV className = "select-course">
            <p id = "hi">Welcome name,</p>
            <p id = "head">Select your course</p>
            <div className = "select">
                <Link to = "SchoolOfComputing"><div id = "course">
                    Computing and Engineering Sciences
                </div><br /></Link>
                <div id = "course">
                    Mass Communication
                </div>
            </div>
        </DIV>
    );
}
 
export default SelectCourse;
