import { PaystackButton } from 'react-paystack'
import React, { useState } from "react"
import styled from 'styled-components';
import { Redirect } from 'react-router-dom'
import bucc from '../image/mass.webp'

const DIV = styled.div`    
 margin: 0 auto;
    margin-top: 80px;
    text-align: center;
    letter-spacing: 0.1rem;
    width: 80%;
    max-width: 600px;
    box-shadow: 0px 0px 30px 0px grey;
    border-radius: 10px;
   
    .item-image {
        width: 30%;
        object-fit: cover;
        padding-top: 5px;
        border-radius: 15px;
    }
    .checkout {
      box-shadow: 0px 0px 40px 0px grey;
      margin: 0 auto;
      padding-top: 30px;
      background: black;
      justify-content: center;
      height: 310px;
      width: 100%;
      border-radius: 10px;
    }
    .checkout-form label {
        margin: 0 auto;
        display: block;
        color: white;
        font-size: 15px;
        margin-bottom: 5px;
        text-align: left;
        width: 60%;
    }   
    input{
        margin: 0 auto;
        display: block;
        border-radius: 3px;
        height: 28px;
        border: none;
        margin-bottom: 15px;
        width: 60%;
    }
    .paystack-button {
        margin: 0 auto;
        cursor: pointer;
        text-align: center;
        font-size: 15px;
        letter-spacing: 0.1rem;
        text-transform: uppercase;
        background-color: #f1356d;
        font-weight: bold;
        color: white;
        border-radius: 5px;
        width: 80%;
        height: 40px;
        margin-top: 20px;
        border: none;
    }
`

const PayComputing = () => {
    const publicKey = `pk_test_f93a2b4fbfdae5834eb1f3fe95728e99cf63b8bd`
    const amount = 50000
    const [email, setEmail] = useState("")
    const [name, setName] = useState("")
    const [phone, setPhone] = useState("")
    const [success, setSuccess] = useState("")

    const componentProps = {
      email,
      amount,
      metadata: {
        name,
        phone,
      },
      publicKey,
      reference: ''+Math.floor((Math.random() * 1000000000000) + 1),
      text: "Pay Now",
      onSuccess: () => {
        alert("Thanks for the purchase")
        setSuccess(true)
      },
      // onClose: () => alert("Hope to see you later")
    }

    if(success === true){
      return <Redirect to = '/MassSteps' />
    }
    if (localStorage.getItem('access_token') === null){
      alert("User not logged In");
      return <Redirect to = '/' />
    }

    return (
        <DIV className="container">
        <img className="item-image"
          src={bucc}
          alt="product"
        />
        <div className="checkout">
          <div className="checkout-form">
            <div className="checkout-field">
              <label>Name</label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Email</label>
              <input
                type="text"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="checkout-field">
              <label>Phone</label>
              <input
                type="text"
                id="phone"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
            </div>
            <PaystackButton className="paystack-button" {...componentProps} />
          </div>
        </div>
      </DIV>
    )
  }
 
export default PayComputing;
