import React from "react";

const PostLoading = (Component) => {
    return function PostLoadingComponent({ isLoading, ...props }) {
        if(!isLoading) return <Component {...props} />;

        else{
            return (
                <p>
                    we are waiting for the data to load!....
                </p>
            )
        }
       
    }
}
export default PostLoading;
