import { Link } from 'react-router-dom';
import styled from 'styled-components'
import { useState } from 'react';

const Ul = styled.ul`
        
    margin-left: auto;
    margin-right: 50px;
    flex-flow: row nowrap;
    display: flex;
    list-style: none;

    li{
        color: white;
        margin-left: 40px;
        padding: 10px;
        text-decoration: none;
        font-size: 20px;
        font-stylr: roboto;
    }

    li:hover {
        color: #f1356d;
    }
    @media (max-width: 700px){
        margin-right: 0;
        position: fixed;
        transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(100%)'} ;
        transition: 0.2s;
        border-radius: 30px;
        flex-flow: column nowrap;
        background-color: #f1356d;
        top: 0;
        right: 0;
        height: 40vh;
        padding-top: 3.5rem;
        padding-left: 0.1rem;
        display: ${({ showing }) => showing ? 'none' : 'flex' };

        li{
            color: white;
            padding-right: 50px;
            padding-top: 60px;
            padding-left: 10px;
        }
        li:hover {
            color: black;
        }
    }
`;

const RightNav = ({ open }) => {
    const [showing, setShowing] = useState(true)
    return ( 
        <Ul open = {open}>
           <Link to = '/' showing = {showing} onClick = {() => setShowing(!showing)}><li >Home</li></Link> 
           <Link to = './AboutUs'><li>About Us</li></Link> 
        </Ul>
    );
}
 
export default RightNav;
